import React, { useState, useEffect } from 'react';
import { View, Text, TextInput, Button, StyleSheet } from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';

const Receipt = () => {
  const [budget, setBudget] = useState('');
  const [expenses, setExpenses] = useState({});
  const [totalExpenses, setTotalExpenses] = useState(0);
  const [remainingBudget, setRemainingBudget] = useState(0);

  useEffect(() => {
    loadExpenses();
  }, []);

  const loadExpenses = async () => {
    try {
      const storedExpenses = await AsyncStorage.getItem('expenses');
      if (storedExpenses !== null) {
        setExpenses(JSON.parse(storedExpenses));
      }
    } catch (error) {
      console.error('Error loading expenses:', error);
    }
  };

  const saveExpenses = async () => {
    try {
      await AsyncStorage.setItem('expenses', JSON.stringify(expenses));
    } catch (error) {
      console.error('Error saving expenses:', error);
    }
  };

  const handleAddExpense = () => {
    if (budget.trim() !== '') {
      const expense = {
        amount: parseFloat(budget),
        date: new Date().toISOString(),
      };
      setExpenses(prevExpenses => {
        const updatedExpenses = { ...prevExpenses };
        updatedExpenses[expense.date] = expense;
        return updatedExpenses;
      });
      setBudget('');
    }
  };

  useEffect(() => {
    const total = Object.values(expenses).reduce(
      (sum, expense) => sum + expense.amount,
      0
    );
    setTotalExpenses(total);
    setRemainingBudget(parseFloat(budget) - total);
  }, [expenses]);

  useEffect(() => {
    saveExpenses();
  }, [expenses]);

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Budget Tracker</Text>
      <Text>Total Expenses: {totalExpenses}</Text>
      <Text>Remaining Budget: {remainingBudget}</Text>
      <TextInput
        style={styles.input}
        placeholder="Enter expense amount"
        value={budget}
        onChangeText={text => setBudget(text)}
        keyboardType="numeric"
      />
      <Button title="Add Expense" onPress={handleAddExpense} />
      {Object.values(expenses).map((expense, index) => (
        <View key={index} style={styles.expense}>
          <Text>Amount: {expense.amount}</Text>
          <Text>Date: {expense.date}</Text>
        </View>
      ))}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  input: {
    height: 40,
    borderColor: 'gray',
    borderWidth: 1,
    marginBottom: 8,
    paddingHorizontal: 8,
  },
  expense: {
    borderWidth: 1,
    borderColor: 'gray',
    padding: 8,
    marginBottom: 8,
  },
});

export default Receipt;
