import React, { createContext, useState } from 'react';

export const Context = createContext();

export const Provider = ({ children }) => {
  const [state, setState] = useState({});

  const updateProfile = (profileData) => {
    // Update the state with the profileData
    setState({ ...state, ...profileData });
  };

  // Return the provider component with the context values and functions
  return (
    <Context.Provider value={{ state, updateProfile }}>
      {children}
    </Context.Provider>
  );
};
