import React, { useState, useEffect, useRef } from 'react';
import { View, Text, Button, Image, TextInput, ScrollView, StyleSheet } from 'react-native';
import { Camera } from 'expo-camera';
import AsyncStorage from '@react-native-async-storage/async-storage';

const Receipt = () => {
  const [images, setImages] = useState([]);
  const cameraRef = useRef(null);
  const [hasPermission, setHasPermission] = useState(null);
  const [cameraType, setCameraType] = useState(Camera.Constants.Type.back);

  useEffect(() => {
    loadImages();
    getCameraPermission();
  }, []);

  const loadImages = async () => {
    try {
      const storedImages = await AsyncStorage.getItem('receiptImages');
      if (storedImages) {
        setImages(JSON.parse(storedImages));
      }
    } catch (error) {
      console.error('Error loading images:', error);
    }
  };

  const saveImages = async (updatedImages) => {
    try {
      await AsyncStorage.setItem('receiptImages', JSON.stringify(updatedImages));
      setImages(updatedImages);
    } catch (error) {
      console.error('Error saving images:', error);
    }
  };

  const getCameraPermission = async () => {
    const { status } = await Camera.requestPermissionsAsync();
    setHasPermission(status === 'granted');
  };

  const takePicture = async () => {
    if (cameraRef.current) {
      const photo = await cameraRef.current.takePictureAsync();
      saveImages([...images, { uri: photo.uri, category: '' }]);
    }
  };

  const handleAddCategory = (index, category) => {
    setImages((prevImages) => {
      const updatedImages = [...prevImages];
      updatedImages[index].category = category;
      return updatedImages;
    });
  };

  const renderImages = () => {
    if (images.length === 0) {
      return <Text>No images found</Text>;
    }

    return images.map((image, index) => (
      <View key={index} style={styles.imageContainer}>
        <Image source={{ uri: image.uri }} style={styles.image} />
        <TextInput
          style={styles.categoryInput}
          placeholder="Enter category"
          value={image.category}
          onChangeText={(text) => handleAddCategory(index, text)}
        />
      </View>
    ));
  };

  return (
    <View style={styles.container}>
      <ScrollView style={styles.scrollView}>{renderImages()}</ScrollView>
      {hasPermission ? (
        <Camera style={styles.camera} type={cameraType} ref={cameraRef} />
      ) : (
        <Text>No access to camera</Text>
      )}
      <Button title="Take Picture" onPress={takePicture} disabled={!hasPermission} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  scrollView: {
    marginBottom: 16,
  },
  imageContainer: {
    marginBottom: 16,
  },
  image: {
    width: '100%',
    height: 200,
    resizeMode: 'cover',
  },
  categoryInput: {
    height: 40,
    borderWidth: 1,
    borderColor: 'gray',
    marginTop: 8,
    padding: 8,
  },
  camera: {
    width: '100%',
    aspectRatio: 1,
    marginBottom: 16,
  },
});

export default Receipt;
