import React, { useState, useEffect } from 'react';
import { View, Text, Button, Image, StyleSheet } from 'react-native';
import * as ImagePicker from 'expo-image-picker';
import AsyncStorage from '@react-native-async-storage/async-storage';

const Receipt = () => {
  const [selectedImage, setSelectedImage] = useState(null);

  useEffect(() => {
    retrieveImageFromStorage();
  }, []);

  const retrieveImageFromStorage = async () => {
    try {
      const imageUri = await AsyncStorage.getItem('selectedImage');
      if (imageUri !== null) {
        setSelectedImage(imageUri);
      }
    } catch (error) {
      console.log('Error retrieving image from AsyncStorage:', error);
    }
  };

  const saveImageToStorage = async (imageUri) => {
    try {
      await AsyncStorage.setItem('selectedImage', imageUri);
    } catch (error) {
      console.log('Error saving image to AsyncStorage:', error);
    }
  };

  const openImagePickerAsync = async () => {
    const permissionResult = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (permissionResult.granted === false) {
      alert('Permission to access camera roll is required!');
      return;
    }

    const pickerResult = await ImagePicker.launchImageLibraryAsync();
    if (!pickerResult.cancelled) {
      setSelectedImage(pickerResult.uri);
      saveImageToStorage(pickerResult.uri);
    }
  };

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Receipt Management</Text>

      <Button title="Select Image" onPress={openImagePickerAsync} />

      {selectedImage && (
        <View style={styles.imageContainer}>
          <Image source={{ uri: selectedImage }} style={styles.image} />
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
    alignItems: 'center',
    justifyContent: 'center',
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    marginBottom: 16,
  },
  imageContainer: {
    marginTop: 16,
    borderWidth: 1,
    borderColor: 'gray',
    borderRadius: 5,
    padding: 8,
    alignItems: 'center',
  },
  image: {
    width: 200,
    height: 200,
    resizeMode: 'contain',
  },
});

export default Receipt;
